    ready:true,
    date:new Date(response.data.dt * 1000),
  description: response.data.weather[0].description,
  humidity: response.data.main.humidity,
  temperature: response.data.main.temp,
  iconUrl: "	https://openweathermap.org/img/wn/02d@2x.png",
  wind: response.data.speed,
  city: response.data.name,
});

setTemperature(Math.round(response.data.main.temp));

      }
if (weatherData.ready){

 
  return (

    <div className="weather">
      <form>
        <div className="row">
          <div className="col-9">
            <input
              type="Search"
              autoFocus="on"
              placeholder="Search City..."
              className="form"
            />
          </div>
          <div className="col-3">
            <input
              type="submit"
              value="Search"
              className="btn btn-primary w-100"
            />
          </div>
        </div>
      </form>

      <ul className="list">
        <h1>{weatherData.city}</h1>

        <ul></ul>
        <ul className="text-capitalize">{weatherData.description}</ul>
      </ul>

      <div className="row">
        <div className="col-6">
          <div className="clearfix">
            <img
              src={weatherData.iconUrl}
              alt={weatherData.description}
            />
            <div className="float-left" />
            <span className="temp">{Math.round(weatherData.temperature)}</span>
            <span className="unit">Â°C {""}</span>
            <span className="fahrenheit">| â</span>
          </div>
        </div>
            <div className="col-6">
        <ul>
          <ul>Humidity: {weatherData.humidity}%</ul>
          <ul>Humidity: 72%</ul>
          <ul>Wind: {weatherData.wind} {""}km/h</ul>
        </ul>
      </div>
      </div>
    </div>
  );
}
 else {
const apiKey = "e2e5f0664e14425e87abaae94f48d5e8";
    let city="Paris";
    const apiUrl =
      `https://api.openweathermap.org/data/2.5/weather?q=${props.city}&appid=${apiKey}&units=metric`;
      axios.get(apiUrl).then(handleResponse);
